import { axios } from 'axios';
import { HOLD_EDIT_USER, UPDATE_STUDENT, UPDATE_USER, HOLD_EDIT_COURSE, HOLD_ADD_STUDENT, HOLD_REMOVE_STUDENT } from '../action/types';
import { HOLD_EDIT_USER_TYPE } from './../action/types';
//Reducer is a switch statement with actions and defines those actions

const initialState = {
    studentList: []
}

//Is it better to return the list from the API call and set state to that, or update state on my own
const tempReducer = (state = initialState, action) => {
    switch (action.type) {
        case UPDATE_USER: 
            
            let newTemp = {
                ...state.editUser
            }
            newTemp.user = action.payload
            return {
                ...state,
                editUser: newTemp
            }
        case UPDATE_STUDENT:
            return {
                ...state,
                editUser: action.payload
            }
        case HOLD_EDIT_USER:
            return {
                ...state,
                editUser: action.payload
            };
        case HOLD_EDIT_USER_TYPE:
            return {
                ...state,
                editUserType: action.payload
            }
        case HOLD_EDIT_COURSE:
            const tempCourse = Object.assign({})
            return {
                ...state,
                editCourse: action.payload
              }
        case HOLD_REMOVE_STUDENT:
            let courseRemoveStudent = {
                ...state.editCourse
            }
            
            const course = courseRemoveStudent.studentList && courseRemoveStudent.studentList.filter(
                function checkId(student) {
                    //console.log(student.id)
                    //console.log(action.payload)
                    return student.id !== action.payload
                }
            )
            courseRemoveStudent.studentList = course

            return {
                ...state,
                editCourse: courseRemoveStudent
            }
        case HOLD_ADD_STUDENT:
            let courseAddStudent = {
                ...state.editCourse
            }
            courseAddStudent.studentList.push(action.payload)
            return {
                ...state,
                editCourse: courseAddStudent
            }
        default:
            return state;
    }
} 

export default tempReducer;
