import axios from 'axios'
import { GET_ALL_COURSES, ADD_COURSE, GET_COURSE } from './types';
import { getAdminUser } from './adminActions';

export const getAllCourses =()=> dispatch => {
    axios.get("http://localhost:8080/course/all")
    .then(response => {
        dispatch({
            type: GET_ALL_COURSES,
            payload: response.data
        })
    })
}

export const addCourse =(course)=> dispatch => {
    axios.post("http://localhost:8080/course/new", course)
    .then(response => {
        dispatch({
            type: GET_ALL_COURSES,
            payload: response.data
        })
    })
}

export const deleteCourse = (courseId) => dispatch => {
    axios.get("http://localhost:8080/course/delete/" + courseId)
    .then(response => {
        dispatch({
            type: GET_ALL_COURSES,
            payload: response.data
        })
    })
}

export const addAssignment = (course, assignment) => dispatch => {
    axios.post("http://localhost:8080/course/addAssignment", course, assignment)
    .then(response => {
        dispatch({
            type: GET_ALL
        })
    })
}

export const editCourseDetails = (course) => dispatch => {
    console.log("ACTION")
    axios.post("http://localhost:8080/course/update", course)
    .then(response => {
        console.log("RESPONSE")
        dispatch({
            type: GET_ALL_COURSES,
            payload: response.data
        })
    })
}

export const addStudentToCourse = (studentId, courseId) => dispatch => {
    axios.post(`http://localhost:8080/course/addStudent/?courseId=${courseId}&studentId=${studentId}`)
    .then(response => {
        dispatch({
            type: GET_COURSE,
            payload: response.data
        })
    })
}
