import React, { Component, useEffect } from 'react';
import Delete from '../../../images/icons/trash.svg';
import Edit from '../../../images/icons/edit.svg';
import saveUser from './../../databaseUtil/databaseUtil';
import axios from 'axios'
import EditUser from './EditUser';
import UserListing from './UserListing';

class AddStudentModal extends Component {
  constructor() {
    super()
    this.handleSubmit = this.handleSubmit.bind(this);
    this.focusRef = React.createRef();
    this.editUser = this.editUser.bind(this)
    this.state = {
      currentEditUserAddress: [],
      currentEditUser: "",
      dob: "",
      firstName: "",
      lastName: "",
      email: "",
      password: "",
      address: "",
      unit: "",
      city: "",
      state: "",
      zipcode: "",
      type: "".toUpperCase(),
      success: "",
      states: [],
      userList: [],
      sideBarUserList: []
    }
  }

  setDob(value) {
    this.setState({
      dob: value
    })
  }

  setEmail(value) {
    this.setState({
      email: value
    })
  }

  setPassword(value) {
    this.setState({
      password: value
    })
  }

  setFirstName(value) {
    this.setState({
      firstName: value
    })
  }

  setLastName(value) {
    this.setState({
      lastName: value
    })
  }

  setAddress(value) {
    this.setState({
      address: value

    })
  }

  setUnit(value) {
    this.setState({

      unit: value

    })
  }

  setCity(value) {
    this.setState({

      city: value

    })
  }

  setUserState(value) {
    this.setState({

      state: value

    })
  }

  setZipcode(value) {
    this.setState({

      zipcode: value

    })
  }

  setType(value) {

    this.setState({
      type: value
    })
  }

  handleSubmit(event) {
    event.preventDefault();

    const user = {
      firstName: this.state.firstName,
      lastName: this.state.lastName,
      dob: this.state.dob,
      email: this.state.email,
      password: this.state.password,
      type: this.state.type ? this.state.type : document.getElementById("type").value,
      contactInformation: [{
        address: this.state.address,
        city: this.state.city,
        state: "MO",
        zipcode: this.state.zipcode
      }]
    }

    saveUser(user)
    
    const temp = this.state.userList;

    this.cancelCourse()
    this.componentDidMount()
    this.setState({
      success: "Success!"
    }
    )
  }

  editUser(user) {

    this.setState({
      currentEditUser: user,
      currentEditUserAddress: user.contactInformation
    })
  }

  deleteUser(id) {
    //delete from database and reset state
    const answer = window.confirm("Are you sure you want to delete user " + id );

    if(answer) {
      axios.get("http://localhost:8080/delete/" + id).then(response => {
      console.log(response.data)
      return response.data
    })
    
    const tempItems = this.state.userList.map(user => {

      return user.key == id ? null : user
    })

    this.setState({
      userList: tempItems
    })
    } else {  
      return 
    }

  }

  cancelCourse = () => {
    document.getElementById("form-to-clear").reset();
  }



  componentDidMount() {
    
    //Get all states to place into the dropdown list
    axios.get("http://localhost:8080/getStates").then(response => {
      const temp = response.data.map(st => {
        return <option>{st}</option>
      })

      this.setState({
        states: temp
      })
    })

    axios.get("http://localhost:8080/all").then(response => {
      const temp = response.data.map(user => {
        const fullName = user.firstName + " " + user.lastName
        return (

          <UserListing 
          key={user.id}
          editUser={()=> this.editUser(user)} 
          deleteUser={()=> this.deleteUser(user.id)}
          text={fullName} />
        )
      })

      this.setState({
        userList: temp
      })
    })
  }

  render() {

    return (
      <div>
        <div data-backdrop="static" className="modal fade" id="addStudentModal" tabIndex={-1} role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
          <div className="modal-dialog mw-100 w-75" role="document">
            <div className="modal-content">
              <div className="modal-header" ref={this.focusRef}>
                <h5 className="modal-title" id="exampleModalLabel">Add or Remove Users</h5>
                <button type="button" className="close" data-dismiss="modal" aria-label="Close">
                  <span aria-hidden="true">×</span>
                </button>
              </div>
              <div className="modal-body">
                <div>
                  <div className="row pl-2 border-bottom">
                    <form className="form-inline" >
                      <div className="input-group mb-2 mr-sm-2">
                        <div className="input-group-prepend">
                          <div className="input-group-text">Search</div>
                        </div>
                        <input type="text" className="form-control" id="inlineFormInputGroupUsername2" placeholder="Username" />
                      </div>
                      {/* Select user dropdown menu */}
                      <div className="input-group mb-2 mr-sm-2">
                        <select className="custom-select my-1 mr-sm-2">
                          <option selected>Choose...</option>
                          <option value={1}>Student</option>
                          <option value={2}>Admin</option>
                          <option value={3}>Parent</option>
                          <option value={4}>Teacher</option>
                        </select>
                      </div>

                      <button type="submit" className="btn btn-outline-primary mb-2">Find</button>
                    </form>

                  </div>
                  <div className="row">
                    <div className="col-md-3 border-right overflow-auto mt-4">

                      {/* List of all users that have been searched for */}
                      {this.state.userList}

                    </div>
                    <div className="col mt-4">
                      <div className="col-md-12">
                        
                        {/* Form to create a new student */}
                        <form onSubmit={this.handleSubmit} id="form-to-clear">
                          <div className="form-row w-100">
                            <div className="form-group col-md-6 w-100">
                              <label htmlFor="email">Email address</label>
                              <input onChange={e => this.setEmail(e.target.value)} type="email" name="email" className="form-control w-100" id="email" aria-describedby="emailHelp" placeholder="Enter email" required />
                              <small id="emailHelp" className="form-text text-muted">
                                We'll never share your email with anyone else.
                                      </small>
                            </div>
                            <div className="form-group col-md-6 w-100">
                              <label htmlFor="password">Password</label>
                              <input onChange={e => this.setPassword(e.target.value)} type="password" name="password" className="form-control w-100" id="password" placeholder="Password" required />
                              <small id="emailHelp" className="form-text text-muted">
                                We'll never share your email with anyone else.
                                      </small>
                            </div>
                          </div>

                          <div className="form-row w-100">

                            <div className="form-group col-md-6 w-100">
                              <label htmlFor="password">First Name</label>
                              <input required onChange={e => this.setFirstName(e.target.value)} type="text" name="firstName" className="form-control w-100" id="firstName" aria-describedby="emailHelp" placeholder="First Name" />
                            </div>

                            <div className="form-group col-md-6 w-100">
                              <label htmlFor="password">Last Name</label>
                              <input required onChange={e => this.setLastName(e.target.value)} type="text" name="lastName" className="form-control w-100" id="lastName" placeholder="Last Name" />
                            </div>
                          </div>
                          <div className="form-group">
                            <label for="inputAddress">Address</label>
                            <input required onChange={e => this.setAddress(e.target.value)} type="text" className="form-control" id="inputAddress" name="address" placeholder="1234 Main St" />
                          </div>
                          <div className="form-row">
                            <div className="form-group col">
                              <label for="inputAddress2">Address 2</label>
                              <input onChange={e => this.setUnit(e.target.value)} type="text" className="form-control" id="inputAddress2" name="unit" placeholder="Apartment, studio, or floor" />
                            </div>
                            <div className="form-group col">
                              <label for="inputAddress2">Date of Birth</label>
                              <input required onChange={e => this.setDob(e.target.value)} type="date" className="form-control" id="inputAddress2" name="unit" placeholder="Apartment, studio, or floor" />

                            </div>
                          </div>
                          <div className="form-row">
                            <div className="form-group col-md-6">
                              <label for="inputCity">City</label>
                              <input required onChange={e => this.setCity(e.target.value)} type="text" className="form-control" name="city" id="inputCity" />
                            </div>
                            <div className="form-group col-md-4">
                              <label for="inputState">State</label>
                              <select required onChange={e => this.setUserState(e.target.value)} id="inputState" name="state" className="form-control">
                                {this.state.states}
                              </select>
                            </div>
                            <div className="form-group col-md-2">
                              <label for="inputZip">Zip</label>
                              <input onChange={e => this.setZipcode(e.target.value)} type="text" name="zipcode" className="form-control" id="inputZip" />
                            </div>
                          </div>


                          <div className="form-row mx-auto">
                            <div className="form-check col">
                              <input className="form-check-input" type="checkbox" id="gridCheck" />
                              <label className="form-check-label" for="gridCheck">
                                Free and Reduced Lunch
                              </label>
                            </div>
                            <div className="form-check col">
                              <input className="form-check-input" type="checkbox" id="gridCheck" />
                              <label className="form-check-label" for="gridCheck">
                                Compromised Housing
                              </label>
                            </div>
                            <div className="form-check col">
                              <input className="form-check-input" type="checkbox" id="gridCheck" />
                              <label className="form-check-label" for="gridCheck">
                                English as a Second Language
                              </label>
                            </div>

                          </div>
                          <div className="form-row">
                            <div className="input-group col">
                              <p className="text-alert">{this.state.message}</p>
                              <select id="type" onChange={e => this.setType(e.target.value)} className="custom-select mt-4" name="type" required>
                                <option value="STUDENT" selected>Student</option>
                                <option value="ADMIN">Admin</option>
                                <option value="PARENT">Parent</option>
                                <option value="TEACHER">Teacher</option>
                              </select>
                            </div>
                            <div className="col">

                              <input type="submit" className="btn btn-primary w-100 mt-4">

                              </input>

                            </div>
                          </div>
                        </form>
                      </div>
                    </div>
                  </div>

                </div>

              </div>
              <div className="modal-footer">
                <h4 className="text-success mr-5">{this.state.success}</h4>
                <button type="button" className="btn btn-secondary" data-dismiss="modal" >Close</button>
              </div>
            </div>
          </div>
        </div>
        
      </div>
    );
  }
}

export default AddStudentModal;
